import React, { useState, useEffect, useRef } from 'react';
import { SendHorizonal } from 'lucide-react';

export default function ChatPage() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [file, setFile] = useState(null);
  const [uploadedFilePath, setUploadedFilePath] = useState('');
  const chatEndRef = useRef(null);

  const handleFileUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      setUploadedFilePath(data.filepath);  // Store returned filepath
      alert("File uploaded successfully!");
    } catch (error) {
      console.error('File upload failed:', error);
      alert("File upload failed");
    }
  };

  const handleSend = async () => {
    if (!input.trim() || !uploadedFilePath) {
      alert('Please upload a file and enter a question.');
      return;
    }

    const userMessage = { sender: 'user', text: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');

    try {
      const response = await fetch('http://localhost:5000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          question: input,
          filepath: uploadedFilePath,
        }),
      });

      const data = await response.json();
      const aiMessage = { sender: 'ai', text: data.reply };
      setMessages((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [
        ...prev,
        { sender: 'ai', text: 'Something went wrong. Please try again.' },
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="h-screen w-full bg-gradient-to-br from-gray-900 via-slate-800 to-gray-900 text-white flex flex-col items-center p-4">
      <div className="w-full max-w-4xl h-full flex flex-col bg-white/10 backdrop-blur-lg rounded-3xl p-6 shadow-2xl overflow-hidden">
        <h1 className="text-3xl font-bold text-center mb-6 text-white drop-shadow">
          🔍 Ask about your Document
        </h1>

        <div className="mb-4 flex items-center gap-4">
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            className="text-white"
          />
          <button
            onClick={handleFileUpload}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg"
          >
            Upload
          </button>
        </div>

        <div className="flex-1 overflow-y-auto space-y-4 px-2 custom-scrollbar">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`transition-all duration-300 ease-in-out max-w-2xl px-4 py-3 rounded-xl text-sm md:text-base whitespace-pre-wrap ${
                msg.sender === 'user'
                  ? 'bg-blue-600 text-white self-end ml-auto rounded-br-none shadow-md'
                  : 'bg-white text-gray-900 self-start mr-auto rounded-bl-none shadow-md'
              }`}
            >
              {msg.text}
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>

        <div className="mt-4 flex items-center gap-2">
          <input
            type="text"
            placeholder="Type your question here..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyPress}
            className="flex-1 px-5 py-3 bg-white/20 text-white placeholder-gray-300 border border-white/30 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 backdrop-blur-sm"
          />
          <button
            onClick={handleSend}
            className="bg-blue-600 hover:bg-blue-700 text-white p-3 rounded-full transition-all shadow-lg"
          >
            <SendHorizonal size={20} />
          </button>
        </div>
      </div>
    </div>
  );
}
