import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { CloudUpload, FileText, LoaderCircle } from 'lucide-react';

export default function Upload() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // for redirection
  let uploadedFilePath = "";

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage('');
  };

  const handleUpload = async () => {
    
    if (!file) {
      setMessage('‚ùå Please select a file first!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      setUploading(true);
      setMessage('');

      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        setMessage(`‚úÖ Upload successful: ${result.message}`);
        setFile(null);

        // Wait 1.5s before navigating to chat page
        setTimeout(() => {
          navigate('/chat');
        }, 1500);
      } else {
        setMessage('‚ùå Upload failed. Please try again.');
      }
    } catch (error) {
      console.error('Upload failed:', error);
      setMessage('‚ùå Upload failed. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-12 p-8 bg-white shadow-2xl rounded-3xl border border-gray-200">
      <h1 className="text-3xl font-bold text-center text-gray-800 mb-6">üìÑ Upload Document</h1>

      <div className="border-2 border-dashed border-gray-300 rounded-xl p-6 hover:border-blue-500 transition-colors">
        <label className="flex flex-col items-center justify-center cursor-pointer">
          <CloudUpload className="w-12 h-12 text-blue-500 mb-2" />
          <p className="text-gray-600 mb-2">Click to select or drag and drop your file here</p>
          <input
            type="file"
            accept=".pdf,.docx,.txt"
            onChange={handleFileChange}
            className="hidden"
          />
        </label>

        {file && (
          <div className="mt-4 flex items-center gap-2 text-sm text-gray-800">
            <FileText className="w-5 h-5 text-blue-500" />
            <span className="truncate max-w-xs">{file.name}</span>
          </div>
        )}
      </div>

      <button
        onClick={handleUpload}
        className={`w-full mt-6 py-2 px-4 rounded-xl text-white font-semibold transition-all ${
          uploading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'
        }`}
        disabled={uploading}
      >
        {uploading ? (
          <div className="flex items-center justify-center gap-2">
            <LoaderCircle className="animate-spin w-5 h-5" />
            Uploading...
          </div>
        ) : (
          'üöÄ Upload'
        )}
      </button>

      {message && (
        <p className="mt-4 text-center text-sm font-medium text-gray-700">{message}</p>
      )}
    </div>
  );
}
