import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Settings, LogOut, Moon, Sun } from 'lucide-react';

const Navbar = ({ darkMode, toggleTheme }) => {
  const [showSettings, setShowSettings] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear session/local storage, then redirect
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <nav className="w-full px-6 py-4 flex justify-between items-center shadow-md bg-white dark:bg-gray-900">
      {/* Logo / Brand */}
      <div className="text-2xl font-bold bg-gradient-to-r from-indigo-500 to-purple-600 text-transparent bg-clip-text">
        SmartDoc AI
      </div>

      {/* Navigation Links */}
      <div className="flex items-center gap-6">
        {/* Home & Analyze Links */}
        <Link
          to="/"
          className="text-gray-800 dark:text-white font-medium hover:text-indigo-600 dark:hover:text-indigo-400 transition"
        >
          Home
        </Link>

        <Link
          to="/analyze"
          className="text-gray-800 dark:text-white font-medium hover:text-purple-600 dark:hover:text-purple-400 transition"
        >
          Analyze
        </Link>

        {/* Login/Signup Buttons */}
        <Link to="/signup">
          <button className="px-5 py-2 rounded-full bg-gradient-to-r from-indigo-500 to-purple-600 text-white font-semibold shadow-md hover:shadow-lg hover:scale-105 transition-all duration-300">
            Sign Up
          </button>
        </Link>

        <Link to="/login">
          <button className="px-5 py-2 rounded-full bg-gradient-to-r from-pink-500 to-red-500 text-white font-semibold shadow-md hover:shadow-lg hover:scale-105 transition-all duration-300">
            Login
          </button>
        </Link>

        {/* Settings Capsule */}
        <div
          className="relative flex items-center px-5 py-2 bg-gray-200 dark:bg-gray-700 rounded-full gap-4 hover:shadow-xl hover:scale-105 transition-all duration-300 cursor-pointer"
          onMouseEnter={() => setShowSettings(true)}
          onMouseLeave={() => setShowSettings(false)}
        >
          {/* Theme Toggle */}
          <button onClick={toggleTheme} className="focus:outline-none text-gray-800 dark:text-white">
            {darkMode ? <Sun size={20} /> : <Moon size={20} />}
          </button>

          {/* Settings Icon & Dropdown */}
          <div className="relative">
            <Settings size={20} className="text-gray-800 dark:text-white" />
            {showSettings && (
              <div className="absolute right-0 top-9 w-36 bg-white dark:bg-gray-800 shadow-lg rounded-md z-10">
                <button
                  onClick={handleLogout}
                  className="w-full px-4 py-2 text-left text-gray-700 dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700 transition"
                >
                  <LogOut size={16} className="inline mr-2" />
                  Logout
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
