import React, { useState } from 'react';
import { Sun, Moon, Settings, LogOut } from 'lucide-react';
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const navigate = useNavigate(); // ðŸ‘ˆ Hook for navigation

  const toggleTheme = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle('dark');
  };

  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  const handleLogout = async () => {
    const result = await Swal.fire({
      title: 'Are you sure?',
      text: 'You want to logout?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'Yes, logout',
      cancelButtonText: 'Cancel',
      confirmButtonColor: '#6366f1', // Indigo
      cancelButtonColor: '#d33',
      background: darkMode ? '#1f2937' : '#fff',
      color: darkMode ? '#fff' : '#000',
    });

    if (result.isConfirmed) {
      // Your logout logic (e.g., clear tokens, reset state)
      console.log('Logged out');

      // Redirect to home
      navigate('/');
    }
  };

  return (
    <nav className="w-full flex items-center justify-between p-4 shadow-md bg-white dark:bg-gray-900 dark:text-white">
      <div className="text-2xl font-bold text-indigo-600 dark:text-indigo-300">
        AI Company
      </div>

      <div className="flex items-center gap-4">
        <a href="/" className="hover:text-indigo-600 transition">Home</a>
        <a href="/upload" className="hover:text-indigo-600 transition">Analyze Document</a>

        {/* Signup & Login buttons */}
        <a href="/signup">
          <button className="bg-indigo-600 text-white px-4 py-1 rounded-full hover:bg-indigo-700 transition">
            Signup
          </button>
        </a>
        <a href="/login">
          <button className="bg-indigo-600 text-white px-4 py-1 rounded-full hover:bg-indigo-700 transition">
            Login
          </button>
        </a>

        {/* Capsule */}
        <div
          className="relative flex items-center px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded-full gap-3 hover:shadow-md transition-all"
          onMouseEnter={() => setShowSettings(true)}
          onMouseLeave={() => setShowSettings(false)}
        >
          <button onClick={toggleTheme} className="focus:outline-none">
            {darkMode ? <Sun size={18} /> : <Moon size={18} />}
          </button>

          <div className="relative">
            <Settings size={18} />
            {showSettings && (
              <div className="absolute right-0 top-8 w-32 bg-white dark:bg-gray-800 shadow-lg rounded-md z-10">
                <button
                  onClick={handleLogout}
                  className="w-full px-4 py-2 text-left hover:bg-gray-100 dark:hover:bg-gray-700 transition"
                >
                  <LogOut size={16} className="inline mr-2" />
                  Logout
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
