import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Sun, Moon, Settings } from 'lucide-react';
import Swal from 'sweetalert2';
import logo from '../assets/logo.png'; // Replace with your logo path

const Navbar = ({ darkMode, toggleDarkMode }) => {
  const navigate = useNavigate();
  const [showSettings, setShowSettings] = useState(false);

  const handleLogout = () => {
    Swal.fire({
      title: 'Are you sure?',
      text: "You will be logged out!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'Yes, logout!',
      cancelButtonText: 'Cancel',
      background: darkMode ? '#1f2937' : '#fff',
      color: darkMode ? '#fff' : '#000',
      confirmButtonColor: '#6366f1',
      cancelButtonColor: '#d33',
      customClass: {
        confirmButton: 'swal2-confirm-btn',
        cancelButton: 'swal2-cancel-btn'
      }
    }).then((result) => {
      if (result.isConfirmed) {
        Swal.fire({
          title: 'Logged out!',
          text: 'You have been logged out successfully.',
          icon: 'success',
          background: darkMode ? '#1f2937' : '#fff',
          color: darkMode ? '#fff' : '#000',
          confirmButtonColor: '#6366f1'
        });
        // logout logic here
      }
    });
  };

  return (
    <nav className={`w-full px-6 py-3 shadow-md flex items-center justify-between ${darkMode ? 'bg-gray-900' : 'bg-white'}`}>
      {/* Left: Logo */}
      <div className="flex items-center gap-2">
        <img src={logo} alt="Company Logo" className="h-10 w-10 rounded-full shadow-md" />
        <span className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>AI Agent</span>
      </div>

      {/* Right: Nav Links & Controls */}
      <div className="flex items-center gap-4">
        {/* Navigation links */}
        <Link to="/" className={`text-sm font-medium hover:underline ${darkMode ? 'text-gray-200' : 'text-gray-800'}`}>
          Home
        </Link>
        <Link to="/upload" className={`text-sm font-medium hover:underline ${darkMode ? 'text-gray-200' : 'text-gray-800'}`}>
          Analyze Document
        </Link>

        {/* Buttons */}
        <button
          onClick={() => navigate('/signup')}
          className="px-4 py-2 rounded-full text-sm font-semibold text-white bg-gradient-to-r from-purple-500 to-pink-500 hover:opacity-90 transition-all shadow-md"
        >
          Sign Up
        </button>

        <button
          onClick={() => navigate('/login')}
          className="px-4 py-2 rounded-full text-sm font-semibold text-white bg-gradient-to-r from-indigo-500 to-blue-500 hover:opacity-90 transition-all shadow-md"
        >
          Login
        </button>

        {/* Capsule: Theme toggle + Settings */}
        <div className="relative">
          <div className={`flex items-center gap-3 px-4 py-2 rounded-full border shadow-sm ${darkMode ? 'bg-gray-800 border-gray-700' : 'bg-gray-100 border-gray-300'}`}>
            {/* Theme toggle */}
            <button onClick={toggleDarkMode}>
              {darkMode ? <Sun size={18} className="text-yellow-400" /> : <Moon size={18} className="text-gray-600" />}
            </button>

            {/* Settings */}
            <button onClick={() => setShowSettings(prev => !prev)}>
              <Settings size={18} className={`${darkMode ? 'text-white' : 'text-gray-800'}`} />
            </button>
          </div>

          {/* Logout Dropdown */}
          {showSettings && (
            <div className={`absolute top-12 right-0 z-50 bg-white dark:bg-gray-800 border dark:border-gray-700 rounded-lg shadow-lg py-2 w-40`}>
              <button
                onClick={handleLogout}
                className="w-full text-left px-4 py-2 text-sm text-red-500 hover:bg-red-50 dark:hover:bg-red-900 dark:text-red-400"
              >
                🚪 Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
