import React, { useState } from 'react';
import { Settings, LogOut, Moon, Sun } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import 'sweetalert2/dist/sweetalert2.min.css';

const Navbar = () => {
  const [showSettings, setShowSettings] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const navigate = useNavigate();

  const handleLogout = async () => {
    const result = await Swal.fire({
      title: 'Are you sure?',
      text: 'Do you want to logout?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#6366f1',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Yes, logout',
      background: darkMode ? '#1f2937' : '#fff',
      color: darkMode ? '#fff' : '#000'
    });

    if (result.isConfirmed) {
      Swal.fire({
        title: 'Logged Out!',
        text: 'You have been successfully logged out.',
        icon: 'success',
        background: darkMode ? '#1f2937' : '#fff',
        color: darkMode ? '#fff' : '#000',
        timer: 1500,
        showConfirmButton: false
      });

      setTimeout(() => navigate('/'), 1500);
    }
  };

  const toggleTheme = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle('dark');
  };

  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  return (
    <nav className="w-full flex items-center justify-between p-4 shadow-md bg-white dark:bg-gray-900 dark:text-white relative z-50">
      {/* Left Logo */}
      <div className="text-2xl font-bold text-indigo-600 dark:text-indigo-300">
        AI Company
      </div>

      {/* Right Side */}
      <div className="flex items-center gap-6">
        <a href="/" className="hover:text-indigo-600 transition">Home</a>
        <a href="/upload" className="hover:text-indigo-600 transition">Analyze</a>

        <a href="/signup">
          <button className="px-4 py-1 bg-indigo-600 hover:bg-indigo-700 text-white rounded-full shadow-md transition-all border-2 border-indigo-600 hover:border-indigo-700">
            Signup
          </button>
        </a>

        <a href="/login">
          <button className="px-4 py-1 bg-white dark:bg-gray-800 text-indigo-600 dark:text-indigo-300 border-2 border-indigo-600 hover:border-indigo-700 rounded-full shadow-md hover:bg-gray-100 dark:hover:bg-gray-700 transition-all">
            Login
          </button>
        </a>

        {/* Capsule */}
        <div className="relative flex items-center px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded-full gap-3 shadow-lg hover:shadow-xl transition cursor-pointer">
          {/* Settings Button */}
          <button onClick={toggleSettings} className="focus:outline-none relative">
            <Settings size={20} />
          </button>

          {/* Settings Dropdown */}
          {showSettings && (
            <div className="absolute right-0 top-12 mt-1 w-40 bg-white dark:bg-gray-800 shadow-lg rounded-md z-50 border dark:border-gray-600">
              <button
                onClick={toggleTheme}
                className="w-full px-4 py-2 flex items-center gap-2 hover:bg-gray-100 dark:hover:bg-gray-700 transition-all"
              >
                {darkMode ? <Sun size={18} /> : <Moon size={18} />}
                {darkMode ? 'Light Mode' : 'Dark Mode'}
              </button>

              <button
                onClick={handleLogout}
                className="w-full px-4 py-2 flex items-center gap-2 hover:bg-gray-100 dark:hover:bg-gray-700 transition-all"
              >
                <LogOut size={18} />
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
