import React, { useState, useEffect } from 'react';
import { Sun, Moon, Settings, LogOut } from 'lucide-react';
import Swal from 'sweetalert2';
import 'sweetalert2/dist/sweetalert2.min.css';

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const handleLogout = () => {
    Swal.fire({
      title: 'Are you sure?',
      text: "You will be logged out!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#6366f1',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Yes, logout!',
      background: darkMode ? '#1f2937' : '#fff',
      color: darkMode ? '#fff' : '#000',
    }).then((result) => {
      if (result.isConfirmed) {
        Swal.fire({
          title: 'Logged out!',
          text: 'You have been logged out successfully.',
          icon: 'success',
          background: darkMode ? '#1f2937' : '#fff',
          color: darkMode ? '#fff' : '#000',
        });
        // Logout logic here
      }
    });
  };

  return (
    <nav className="w-full flex items-center justify-between px-6 py-4 shadow-md bg-white dark:bg-gray-900 dark:text-white transition-colors">
      {/* Logo */}
      <div className="text-2xl font-bold text-indigo-600 dark:text-indigo-300 tracking-wide">
        AI Company ðŸš€
      </div>

      {/* Links + Controls */}
      <div className="flex items-center gap-6">
        <a href="/" className="hover:text-indigo-500 transition font-medium">Home</a>
        <a href="/upload" className="hover:text-indigo-500 transition font-medium">Analyze</a>
        <a href="/signup" className="hover:text-indigo-500 transition font-medium">Signup</a>
        <a href="/login" className="hover:text-indigo-500 transition font-medium">Login</a>

        {/* Capsule */}
        <div
          className="relative group flex items-center px-4 py-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition"
        >
          {/* Theme Toggle */}
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="focus:outline-none"
            title="Toggle Theme"
          >
            {darkMode ? <Sun size={18} /> : <Moon size={18} />}
          </button>

          {/* Settings Dropdown */}
          <div className="ml-3 relative">
            <Settings size={18} className="cursor-pointer" />

            {/* Logout on hover dropdown */}
            <div className="absolute right-0 mt-2 w-36 hidden group-hover:block z-20">
              <button
                onClick={handleLogout}
                className="w-full text-sm bg-white dark:bg-gray-800 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-md px-4 py-2 text-left flex items-center gap-2 shadow-xl"
              >
                <LogOut size={16} />
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
