import React, { useState } from 'react';
import { Sun, Moon, Settings, LogOut } from 'lucide-react';

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [showSettings, setShowSettings] = useState(false);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle('dark');
  };

  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  const handleLogout = () => {
    // Add your logout logic here
    alert('Logged out');
  };

  return (
    <nav className="w-full flex items-center justify-between p-4 shadow-md bg-white dark:bg-gray-900 dark:text-white">
      {/* Left Logo */}
      <div className="text-2xl font-bold text-indigo-600 dark:text-indigo-300">
        AI Company
      </div>

      {/* Right Side */}
      <div className="flex items-center gap-4">
        <a href="/" className="hover:text-indigo-600 transition">Home</a>
        <a href="/upload" className="hover:text-indigo-600 transition">Analyze Document</a>
        <a href="/signup" className="hover:text-indigo-600 transition">Signup</a>
        <a href="/login" className="hover:text-indigo-600 transition">Login</a>

        {/* Capsule */}
        <div className="relative flex items-center px-3 py-1 bg-gray-200 dark:bg-gray-700 rounded-full gap-3">
          {/* Theme Toggle */}
          <button onClick={toggleTheme} className="focus:outline-none">
            {darkMode ? <Sun size={18} /> : <Moon size={18} />}
          </button>

          {/* Settings Button */}
          <button onClick={toggleSettings} className="focus:outline-none relative">
            <Settings size={18} />
          </button>

          {/* Dropdown */}
          {showSettings && (
            <div className="absolute right-0 top-10 mt-1 w-32 bg-white dark:bg-gray-800 shadow-lg rounded-md z-10">
              <button
                onClick={handleLogout}
                className="w-full px-4 py-2 text-left hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                <LogOut size={16} className="inline mr-2" />
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
