import React, { useState } from 'react';

const ChatPage = () => {
  const [messages, setMessages] = useState([
    { sender: 'user', text: 'Hey, how does SmartDoc AI work?' },
    { sender: 'ai', text: 'SmartDoc AI helps you understand your internal documents by asking questions.' },
  ]);
  const [input, setInput] = useState('');

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessage = { sender: 'user', text: input };
    setMessages(prev => [...prev, newMessage]);
    setInput('');

    try {
      const response = await fetch('http://localhost:5000/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question: input }),
      });

      const data = await response.json();
      setMessages(prev => [...prev, { sender: 'ai', text: data.answer }]);
    } catch (error) {
      setMessages(prev => [...prev, { sender: 'ai', text: '‚ö†Ô∏è Failed to get response' }]);
    }
  };

  const handleExportAndEmail = async () => {
    const email = prompt('Enter your email to receive this chat:');
    if (!email || !email.includes('@')) return alert('Please enter a valid email.');

    try {
      const response = await fetch('http://localhost:5000/export-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, messages }),
      });

      const data = await response.json();
      if (data.success) {
        alert('‚úÖ Chat successfully exported and sent to your email!');
      } else {
        alert('‚ùå Failed to send email. Try again later.');
      }
    } catch (error) {
      console.error(error);
      alert('‚ö†Ô∏è Something went wrong while exporting.');
    }
  };

  return (
    <div className="p-8 bg-gray-50 min-h-screen flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-4 text-blue-700">üí¨ SmartDoc AI Chat</h1>

      <div className="w-full max-w-3xl bg-white rounded-xl shadow-md p-6 space-y-4 overflow-y-auto max-h-[60vh]">
        {messages.map((msg, idx) => (
          <div key={idx} className={`text-sm p-2 rounded-md ${msg.sender === 'user' ? 'bg-blue-100 text-right ml-auto w-fit' : 'bg-green-100 text-left mr-auto w-fit'}`}>
            <strong>{msg.sender === 'user' ? 'You' : 'SmartDoc AI'}:</strong>
            <div>{msg.text}</div>
          </div>
        ))}
      </div>

      {/* Input Area */}
      <div className="flex w-full max-w-3xl mt-6">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          className="flex-1 border border-gray-300 rounded-l-full px-4 py-2 focus:outline-none"
          placeholder="Type your question here..."
        />
        <button
          onClick={handleSend}
          className="bg-blue-600 text-white px-6 py-2 rounded-r-full hover:bg-blue-700"
        >
          Send
        </button>
      </div>

      {/* Export & Email Button */}
      <div className="mt-4 flex justify-end w-full max-w-3xl">
        <button
          onClick={handleExportAndEmail}
          className="bg-green-600 hover:bg-green-700 text-white px-5 py-2 rounded-full transition-all text-sm shadow-md"
        >
          üì§ Export & Email Chat
        </button>
      </div>
    </div>
  );
};

export default ChatPage;
