import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Home, Upload, Sun, Moon, LogOut } from 'lucide-react';

function Navbar({ darkMode, toggleTheme }) {
  const [showSettings, setShowSettings] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    // Optional: remove auth token or session here
    navigate('/login');
  };

  // Close dropdown on outside click
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest('.user-capsule')) {
        setShowSettings(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <nav className="flex justify-between items-center px-6 py-3 bg-white dark:bg-gray-900 shadow-md fixed w-full z-50">
      {/* Left: Logo or Brand */}
      <div className="text-2xl font-bold text-gray-800 dark:text-white">
        AI Doc Agent
      </div>

      {/* Center: Navigation Links */}
      <div className="flex items-center gap-6">
        <Link
          to="/"
          className="flex items-center gap-1 text-gray-700 dark:text-white hover:text-blue-600 dark:hover:text-blue-400 transition"
        >
          <Home size={18} />
          Home
        </Link>
        <Link
          to="/upload"
          className="flex items-center gap-1 text-gray-700 dark:text-white hover:text-blue-600 dark:hover:text-blue-400 transition"
        >
          <Upload size={18} />
          Analyze
        </Link>
      </div>

      {/* Right: Theme toggle + User capsule */}
      <div className="flex items-center gap-6">
        {/* Theme toggle */}
        <button
          onClick={toggleTheme}
          className="text-gray-700 dark:text-white hover:text-blue-600 dark:hover:text-blue-400 transition"
        >
          {darkMode ? <Sun size={20} /> : <Moon size={20} />}
        </button>

        {/* User Capsule */}
        <div
          className="relative user-capsule z-50"
          onMouseEnter={() => setShowSettings(true)}
          onMouseLeave={() => setShowSettings(false)}
        >
          <div className="flex items-center px-5 py-2 bg-gray-200 dark:bg-gray-700 rounded-full gap-4 text-sm sm:text-base font-medium text-gray-800 dark:text-white hover:shadow-xl hover:scale-105 transition-all duration-300 cursor-pointer">
            <div className="h-10 w-10 rounded-full bg-blue-500 text-white flex items-center justify-center text-lg font-bold">
              U
            </div>
            <span>User Name</span>
          </div>

          {/* Dropdown */}
          {showSettings && (
            <div className="absolute right-0 top-12 w-36 bg-white dark:bg-gray-800 shadow-lg rounded-md z-50">
              <button
                onClick={handleLogout}
                className="w-full px-4 py-2 text-left text-gray-700 dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700 transition"
              >
                <LogOut size={16} className="inline mr-2" />
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
